mapper.delete:
delete from da_grow_cost
where id  in

	#{id }

mapper.update:
    update da_grow_cost
		
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.commonFieldId,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.name,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.cropTypeCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.cropTypeText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.strainsCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.strainsText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.inputCost,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.inputCostUnitCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.inputCostUnitText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.inputCostUnit,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.artificialCost,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.artificialCostUnitCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.artificialCostUnitText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.artificialCostUnit,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.otherCost,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.otherCostUnitCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.otherCostUnitText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.otherCostUnit,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.totalCost,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.totalCostUnitCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.totalCostUnitText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.totalCostUnit,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.acreCost,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.acreCostUnitCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.acreCostUnitText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.acreCostUnit,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.cattyCost,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.cattyCostUnitCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.cattyCostUnitText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daGrowCost.id} then #{daGrowCost.cattyCostUnit,jdbcType=NUMERIC}
                  
              
         
    
    where id in
		
			#{daGrowCost.id,jdbcType=VARCHAR}

mapper.select:
          select
                
          from da_grow_cost where 1 = 1
        
			and id LIKE CONCAT('%',#{EntityRelated.id,jdbcType=VARCHAR},'%')
		

		
				and acre_cost_unit_code = #{EntityRelated.acreCostUnitCode,jdbcType=TINYINT} 
		
		
 				and  acre_cost_unit_text LIKE CONCAT('%',#{EntityRelated.acreCostUnitText,jdbcType=VARCHAR},'%') 
		
		
				and artificial_cost_unit_code = #{EntityRelated.artificialCostUnitCode,jdbcType=TINYINT} 
		
		
 				and  artificial_cost_unit_text LIKE CONCAT('%',#{EntityRelated.artificialCostUnitText,jdbcType=VARCHAR},'%') 
		
		
				and catty_cost_unit_code = #{EntityRelated.cattyCostUnitCode,jdbcType=TINYINT} 
		
		
 				and  catty_cost_unit_text LIKE CONCAT('%',#{EntityRelated.cattyCostUnitText,jdbcType=VARCHAR},'%') 
		
		
				and common_field_id = #{EntityRelated.commonFieldId,jdbcType=VARCHAR} 
		
		
				and crop_type_code = #{EntityRelated.cropTypeCode,jdbcType=TINYINT} 
		
		
 				and  crop_type_text LIKE CONCAT('%',#{EntityRelated.cropTypeText,jdbcType=VARCHAR},'%') 
		
		
				and id = #{EntityRelated.id,jdbcType=VARCHAR} 
		
		
				and input_cost_unit_code = #{EntityRelated.inputCostUnitCode,jdbcType=TINYINT} 
		
		
 				and  input_cost_unit_text LIKE CONCAT('%',#{EntityRelated.inputCostUnitText,jdbcType=VARCHAR},'%') 
		
		
 				and  name LIKE CONCAT('%',#{EntityRelated.name,jdbcType=VARCHAR},'%') 
		
		
				and other_cost_unit_code = #{EntityRelated.otherCostUnitCode,jdbcType=TINYINT} 
		
		
 				and  other_cost_unit_text LIKE CONCAT('%',#{EntityRelated.otherCostUnitText,jdbcType=VARCHAR},'%') 
		
		
				and strains_code = #{EntityRelated.strainsCode,jdbcType=TINYINT} 
		
		
 				and  strains_text LIKE CONCAT('%',#{EntityRelated.strainsText,jdbcType=VARCHAR},'%') 
		
		
				and total_cost_unit_code = #{EntityRelated.totalCostUnitCode,jdbcType=TINYINT} 
		
		
 				and  total_cost_unit_text LIKE CONCAT('%',#{EntityRelated.totalCostUnitText,jdbcType=VARCHAR},'%') 
		


		
			order by
			
				${Order.columnName}
				
					ASC
				
				
					DESC
