mapper.select:
          select
                
          from da_import_export where 1 = 1
        
			and id LIKE CONCAT('%',#{EntityRelated.id,jdbcType=VARCHAR},'%')
		

		
				and common_field_id = #{EntityRelated.commonFieldId,jdbcType=VARCHAR} 
		
		
				and crop_type_code = #{EntityRelated.cropTypeCode,jdbcType=TINYINT} 
		
		
 				and  crop_type_text LIKE CONCAT('%',#{EntityRelated.cropTypeText,jdbcType=VARCHAR},'%') 
		
		
				and export_amount_unit_code = #{EntityRelated.exportAmountUnitCode,jdbcType=TINYINT} 
		
		
 				and  export_amount_unit_text LIKE CONCAT('%',#{EntityRelated.exportAmountUnitText,jdbcType=VARCHAR},'%') 
		
		
				and export_country_code = #{EntityRelated.exportCountryCode,jdbcType=TINYINT} 
		
		
 				and  export_country_text LIKE CONCAT('%',#{EntityRelated.exportCountryText,jdbcType=VARCHAR},'%') 
		
		
 				and  export_enterprise LIKE CONCAT('%',#{EntityRelated.exportEnterprise,jdbcType=VARCHAR},'%') 
		
		
				and export_price_unit_code = #{EntityRelated.exportPriceUnitCode,jdbcType=TINYINT} 
		
		
 				and  export_price_unit_text LIKE CONCAT('%',#{EntityRelated.exportPriceUnitText,jdbcType=VARCHAR},'%') 
		
		
				 AND  unix_timestamp(export_time)>=unix_timestamp(#{EntityRelated.beginExportTime}) 
		
		
				 AND  unix_timestamp(export_time)<unix_timestamp(#{EntityRelated.endExportTime}) 
		
		
				and export_volume_unit_code = #{EntityRelated.exportVolumeUnitCode,jdbcType=TINYINT} 
		
		
 				and  export_volume_unit_text LIKE CONCAT('%',#{EntityRelated.exportVolumeUnitText,jdbcType=VARCHAR},'%') 
		
		
				and id = #{EntityRelated.id,jdbcType=VARCHAR} 
		
		
				and import_amount_unit_code = #{EntityRelated.importAmountUnitCode,jdbcType=TINYINT} 
		
		
 				and  import_amount_unit_text LIKE CONCAT('%',#{EntityRelated.importAmountUnitText,jdbcType=VARCHAR},'%') 
		
		
				and import_country_code = #{EntityRelated.importCountryCode,jdbcType=TINYINT} 
		
		
 				and  import_country_text LIKE CONCAT('%',#{EntityRelated.importCountryText,jdbcType=VARCHAR},'%') 
		
		
 				and  import_enterprise LIKE CONCAT('%',#{EntityRelated.importEnterprise,jdbcType=VARCHAR},'%') 
		
		
				and import_price_unit_code = #{EntityRelated.importPriceUnitCode,jdbcType=TINYINT} 
		
		
 				and  import_price_unit_text LIKE CONCAT('%',#{EntityRelated.importPriceUnitText,jdbcType=VARCHAR},'%') 
		
		
				 AND  unix_timestamp(import_time)>=unix_timestamp(#{EntityRelated.beginImportTime}) 
		
		
				 AND  unix_timestamp(import_time)<unix_timestamp(#{EntityRelated.endImportTime}) 
		
		
				and import_volume_unit_code = #{EntityRelated.importVolumeUnitCode,jdbcType=TINYINT} 
		
		
 				and  import_volume_unit_text LIKE CONCAT('%',#{EntityRelated.importVolumeUnitText,jdbcType=VARCHAR},'%') 
		
		
 				and  name LIKE CONCAT('%',#{EntityRelated.name,jdbcType=VARCHAR},'%') 
		
		
 				and  product_name LIKE CONCAT('%',#{EntityRelated.productName,jdbcType=VARCHAR},'%') 
		
		
				and strains_code = #{EntityRelated.strainsCode,jdbcType=TINYINT} 
		
		
 				and  strains_text LIKE CONCAT('%',#{EntityRelated.strainsText,jdbcType=VARCHAR},'%') 
		


		
			order by
			
				${Order.columnName}
				
					ASC
				
				
					DESC
