mapper.select:
          select
                
          from da_industry_statistics where 1 = 1
        
			and id LIKE CONCAT('%',#{EntityRelated.id,jdbcType=VARCHAR},'%')
		

		
				and common_field_id = #{EntityRelated.commonFieldId,jdbcType=VARCHAR} 
		
		
				and crop_type_code = #{EntityRelated.cropTypeCode,jdbcType=TINYINT} 
		
		
 				and  crop_type_text LIKE CONCAT('%',#{EntityRelated.cropTypeText,jdbcType=VARCHAR},'%') 
		
		
				and fruithanging_area_code = #{EntityRelated.fruithangingAreaCode,jdbcType=TINYINT} 
		
		
 				and  fruithanging_area_text LIKE CONCAT('%',#{EntityRelated.fruithangingAreaText,jdbcType=VARCHAR},'%') 
		
		
				and grow_area_code = #{EntityRelated.growAreaCode,jdbcType=TINYINT} 
		
		
 				and  grow_area_text LIKE CONCAT('%',#{EntityRelated.growAreaText,jdbcType=VARCHAR},'%') 
		
		
				and id = #{EntityRelated.id,jdbcType=VARCHAR} 
		
		
 				and  name LIKE CONCAT('%',#{EntityRelated.name,jdbcType=VARCHAR},'%') 
		
		
				and online_saleamount_code = #{EntityRelated.onlineSaleamountCode,jdbcType=TINYINT} 
		
		
 				and  online_saleamount_text LIKE CONCAT('%',#{EntityRelated.onlineSaleamountText,jdbcType=VARCHAR},'%') 
		
		
				and online_sales_code = #{EntityRelated.onlineSalesCode,jdbcType=TINYINT} 
		
		
 				and  online_sales_text LIKE CONCAT('%',#{EntityRelated.onlineSalesText,jdbcType=VARCHAR},'%') 
		
		
				and organic_area_code = #{EntityRelated.organicAreaCode,jdbcType=TINYINT} 
		
		
 				and  organic_area_text LIKE CONCAT('%',#{EntityRelated.organicAreaText,jdbcType=VARCHAR},'%') 
		
		
				and output_per_total_code = #{EntityRelated.outputPerTotalCode,jdbcType=TINYINT} 
		
		
 				and  output_per_total_text LIKE CONCAT('%',#{EntityRelated.outputPerTotalText,jdbcType=VARCHAR},'%') 
		
		
				and output_total_code = #{EntityRelated.outputTotalCode,jdbcType=TINYINT} 
		
		
 				and  output_total_text LIKE CONCAT('%',#{EntityRelated.outputTotalText,jdbcType=VARCHAR},'%') 
		
		
				and output_value_code = #{EntityRelated.outputValueCode,jdbcType=TINYINT} 
		
		
 				and  output_value_text LIKE CONCAT('%',#{EntityRelated.outputValueText,jdbcType=VARCHAR},'%') 
		
		
				and strains_code = #{EntityRelated.strainsCode,jdbcType=TINYINT} 
		
		
 				and  strains_text LIKE CONCAT('%',#{EntityRelated.strainsText,jdbcType=VARCHAR},'%') 
		
		
				and wholesale_sales_code = #{EntityRelated.wholesaleSalesCode,jdbcType=TINYINT} 
		
		
 				and  wholesale_sales_text LIKE CONCAT('%',#{EntityRelated.wholesaleSalesText,jdbcType=VARCHAR},'%') 
		


		
			order by
			
				${Order.columnName}
				
					ASC
				
				
					DESC
