mapper.delete:
delete from da_sale_peasant
where id  in

	#{id }

mapper.update:
    update da_sale_peasant
		
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.commonFieldId,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.name,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.cropTypeCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.cropTypeText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.strainsCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.strainsText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.tradeAmount,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.tradeAmountUnitCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.tradeAmountUnitText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.tradeAmountUnit,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.tradeVolume,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.tradeVolumeUnitCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.tradeVolumeUnitText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.tradeVolumeUnit,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.unitPrice,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.perPriceUnitCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.perPriceUnitText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.perPriceUnit,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.saleRegionCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.saleRegionText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.saleAreaCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.saleAreaText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.sale,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSalePeasant.id} then #{daSalePeasant.tradeTime,jdbcType=TIMESTAMP}
                  
              
         
    
    where id in
		
			#{daSalePeasant.id,jdbcType=VARCHAR}

mapper.select:
          select
                
          from da_sale_peasant where 1 = 1
        
			and id LIKE CONCAT('%',#{EntityRelated.id,jdbcType=VARCHAR},'%')
		

		
				and common_field_id = #{EntityRelated.commonFieldId,jdbcType=VARCHAR} 
		
		
				and crop_type_code = #{EntityRelated.cropTypeCode,jdbcType=TINYINT} 
		
		
 				and  crop_type_text LIKE CONCAT('%',#{EntityRelated.cropTypeText,jdbcType=VARCHAR},'%') 
		
		
				and id = #{EntityRelated.id,jdbcType=VARCHAR} 
		
		
 				and  name LIKE CONCAT('%',#{EntityRelated.name,jdbcType=VARCHAR},'%') 
		
		
				and per_price_unit_code = #{EntityRelated.perPriceUnitCode,jdbcType=TINYINT} 
		
		
 				and  per_price_unit_text LIKE CONCAT('%',#{EntityRelated.perPriceUnitText,jdbcType=VARCHAR},'%') 
		
		
 				and  sale LIKE CONCAT('%',#{EntityRelated.sale,jdbcType=VARCHAR},'%') 
		
		
				and sale_area_code = #{EntityRelated.saleAreaCode,jdbcType=TINYINT} 
		
		
 				and  sale_area_text LIKE CONCAT('%',#{EntityRelated.saleAreaText,jdbcType=VARCHAR},'%') 
		
		
				and sale_region_code = #{EntityRelated.saleRegionCode,jdbcType=TINYINT} 
		
		
 				and  sale_region_text LIKE CONCAT('%',#{EntityRelated.saleRegionText,jdbcType=VARCHAR},'%') 
		
		
				and strains_code = #{EntityRelated.strainsCode,jdbcType=TINYINT} 
		
		
 				and  strains_text LIKE CONCAT('%',#{EntityRelated.strainsText,jdbcType=VARCHAR},'%') 
		
		
				and trade_amount_unit_code = #{EntityRelated.tradeAmountUnitCode,jdbcType=TINYINT} 
		
		
 				and  trade_amount_unit_text LIKE CONCAT('%',#{EntityRelated.tradeAmountUnitText,jdbcType=VARCHAR},'%') 
		
		
				 AND  unix_timestamp(trade_time)>=unix_timestamp(#{EntityRelated.beginTradeTime}) 
		
		
				 AND  unix_timestamp(trade_time)<unix_timestamp(#{EntityRelated.endTradeTime}) 
		
		
				and trade_volume_unit_code = #{EntityRelated.tradeVolumeUnitCode,jdbcType=TINYINT} 
		
		
 				and  trade_volume_unit_text LIKE CONCAT('%',#{EntityRelated.tradeVolumeUnitText,jdbcType=VARCHAR},'%') 
		


		
			order by
			
				${Order.columnName}
				
					ASC
				
				
					DESC
