mapper.delete:
delete from da_sale_process
where id  in

	#{id }

mapper.update:
    update da_sale_process
		
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.commonFieldId,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.processProduct,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.name,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.processTypeCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.processTypeText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.cropTypeCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.cropTypeText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.processStrainsCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.processStrainsText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.saleRegionCode,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.saleRegionText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.saleAreaCode,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.saleAreaText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.saleAmount,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.saleAmountUnitCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.saleAmountUnitText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.saleAmountUnit,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.saleVolume,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.saleVolumeUnitCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.saleVolumeUnitText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.saleVolumeUnit,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.wasteAmount,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.wasteAmountUnitCode,jdbcType=TINYINT}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.wasteAmountUnitText,jdbcType=VARCHAR}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.wasteAmountUnit,jdbcType=NUMERIC}
                  
              
         
         
              
                  
                     when id=#{daSaleProcess.id} then #{daSaleProcess.saleTime,jdbcType=TIMESTAMP}
                  
              
         
    
    where id in
		
			#{daSaleProcess.id,jdbcType=VARCHAR}

mapper.select:
          select
                
          from da_sale_process where 1 = 1
        
			and id LIKE CONCAT('%',#{EntityRelated.id,jdbcType=VARCHAR},'%')
		

		
				and common_field_id = #{EntityRelated.commonFieldId,jdbcType=VARCHAR} 
		
		
				and crop_type_code = #{EntityRelated.cropTypeCode,jdbcType=TINYINT} 
		
		
 				and  crop_type_text LIKE CONCAT('%',#{EntityRelated.cropTypeText,jdbcType=VARCHAR},'%') 
		
		
				and id = #{EntityRelated.id,jdbcType=VARCHAR} 
		
		
 				and  name LIKE CONCAT('%',#{EntityRelated.name,jdbcType=VARCHAR},'%') 
		
		
 				and  process_product LIKE CONCAT('%',#{EntityRelated.processProduct,jdbcType=VARCHAR},'%') 
		
		
				and process_strains_code = #{EntityRelated.processStrainsCode,jdbcType=TINYINT} 
		
		
 				and  process_strains_text LIKE CONCAT('%',#{EntityRelated.processStrainsText,jdbcType=VARCHAR},'%') 
		
		
				and process_type_code = #{EntityRelated.processTypeCode,jdbcType=TINYINT} 
		
		
 				and  process_type_text LIKE CONCAT('%',#{EntityRelated.processTypeText,jdbcType=VARCHAR},'%') 
		
		
				and sale_amount_unit_code = #{EntityRelated.saleAmountUnitCode,jdbcType=TINYINT} 
		
		
 				and  sale_amount_unit_text LIKE CONCAT('%',#{EntityRelated.saleAmountUnitText,jdbcType=VARCHAR},'%') 
		
		
				and sale_area_code = #{EntityRelated.saleAreaCode,jdbcType=TINYINT} 
		
		
 				and  sale_area_text LIKE CONCAT('%',#{EntityRelated.saleAreaText,jdbcType=VARCHAR},'%') 
		
		
				and sale_region_code = #{EntityRelated.saleRegionCode,jdbcType=TINYINT} 
		
		
 				and  sale_region_text LIKE CONCAT('%',#{EntityRelated.saleRegionText,jdbcType=VARCHAR},'%') 
		
		
				 AND  unix_timestamp(sale_time)>=unix_timestamp(#{EntityRelated.beginSaleTime}) 
		
		
				 AND  unix_timestamp(sale_time)<unix_timestamp(#{EntityRelated.endSaleTime}) 
		
		
				and sale_volume_unit_code = #{EntityRelated.saleVolumeUnitCode,jdbcType=TINYINT} 
		
		
 				and  sale_volume_unit_text LIKE CONCAT('%',#{EntityRelated.saleVolumeUnitText,jdbcType=VARCHAR},'%') 
		
		
				and waste_amount_unit_code = #{EntityRelated.wasteAmountUnitCode,jdbcType=TINYINT} 
		
		
 				and  waste_amount_unit_text LIKE CONCAT('%',#{EntityRelated.wasteAmountUnitText,jdbcType=VARCHAR},'%') 
		


		
			order by
			
				${Order.columnName}
				
					ASC
				
				
					DESC
